INSERT INTO `vidoco_vi_news_detail` VALUES
(21, '', '', '<h2>Đang cập nhật</h2>', '', '', '', 2, '', 0, 1, 1, 1), 
(22, '', '', '<p>Được xây dựng dựa trên quy định bộ nhận dạng thương hiệu của Công ty Cao Group từ tông màu chủ đạo cho đến các họa tiết trang trí, giao diện website của Công ty vừa ấn tượng, bắt mắt nhưng lại rất thân thiện, tạo nên điểm nhấn và sự liên kết đồng bộ với hệ thống nhận diện quy chuẩn của Cao Group.</p><p>Với mục tiêu xây dựng website của Cao Group trở thành kênh thông tin giúp khẳng định uy tín và hình ảnh của Cao Group trên thị trường Việt Nam, đặc biệt là qua hệ thống thông tin điện tử internet, trở thành trang web doanh nghiệp hàng đầu hoạt động trong lĩnh vực bất động sản nghỉ dưỡng và hoạt động giải trí mặt nước.&nbsp;</p><p>Vinh dự là đối tác được Cao Group lựa chọn cho dịch vụ tư vấn và xây dựng website, sau phiên bản website này, Công ty Cổ phần Công Nghệ VIDOCO - một trong những thương hiệu hàng đầu trong lĩnh vực tư vấn và thiết kế phần mềm tại Việt Nam sẽ tiếp tục hợp tác&nbsp; với Cao Group để nghiên cứu, xây dựng và cho ra mắt những phần mềm ứng dụng nhằm phục vụ tốt hơn&nbsp; cho hoạt động quản lý của Công ty từ đó góp phần khẳng định thương hiệu của&nbsp; Cao Group vững mạnh trên thị trường Việt Nam.</p>', '', '', '', 2, '', 0, 1, 1, 1), 
